<script>
export default {
  props: ['undian'],
  data(){
    return {
      title: '',
      num: '',
      image: 'data:image/jpeg;base64,'
    }
  },
  methods: {
    randomList() {
      return this.peserta.slice(0,5).sort(function(){ return 0.5 - Math.random() })
    },
    handleImg(img){
      const selectedImg = img.target.files[0]
      this.createImg(selectedImg)

    },
    createImg(fileObject){
      const reader = new FileReader()

      reader.onload = (e) => {
        this.image = e.target.result
      }
      reader.readAsDataURL(fileObject)
    },
    save(){
      this.$emit('update', {title: this.title, num: this.num, image: this.image})
    }
  }
}
</script>

<template>
  <h1>Kelola Undian</h1>
  <div class="row">
    <div class="col-4">
      <form action="" @submit.prevent>
        <div class="card">
          <img :src="image" v-show="image" class="card-img-top">
          <div class="card-body">
            <h5 class="card-title">Tambah data undian</h5>
            <div class="form-group">
              <label for="namaUndian">Nama Undian</label>
              <input type="text" name="namaUndian" id="namaUndian" class="form-control" v-model="title">
            </div>
            <div class="form-group">
              <label for="jumlahPemenang">Jumlah Pemenang</label>
              <input type="number" name="jumlahPemenang" id="jumlahPemenang" class="form-control" v-model="num">
            </div>
            <div class="form-group">
              <label for="gambarUndian">Gambar Undian</label>
              <input type="file" accept="image/*" name="gambarUndian" id="gambarUndian" @change="handleImg">
            </div>
          </div>
          <div class="card-footer">
            <button class="btn btn-secondary" @click="save()">Submit</button>
          </div>
        </div>
      </form>
    </div>
    <div class="col-8">
      <div class="card-columns">
        <div class="card" v-for="und in undian">
          <img :src="und.image" class="card-img-top">
          <div class="card-body">
            <h5 class="card-title">{{ und.title }}</h5>
            <p>{{ und.num }} Pemenang</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

